let allowSubs=["/archives/","/posts/","/categories/","/tags/"];let matchpath=window.location.pathname.match(/\/.*\//)||[];let firstPath=matchpath[0];let shouldOpen=false;if(allowSubs.includes(firstPath)&&self==top){let t=document.createElement("style");t.innerText=`.long-frame{width:500px;height:600px;border:none;position:absolute;z-index:9999;display:block;}.frame-on{display:block}`;let e=document.createElement("iframe");e.id="longframe";e.classList.add("long-frame");let l=document.getElementsByTagName("body")[0];l.append(t);l.append(e);runscript()}function runscript(){let e=document.getElementById("longframe");this.document.querySelectorAll("a").forEach(t=>{t.addEventListener("mouseover",t=>showInIframe(e,t))});this.document.onclick=function(t){shouldOpen=false;if(e.src!=""){e.style.height=0;e.style.width=0;e.style.display="none"}}}function showInIframe(l,o){if(shouldOpen){return}shouldOpen=true;let t=o.target.href||"";if(!t){return}t=t.split("#")[0];if(t.trimStart().startsWith("http")){if(window.location.host==t.replace(/^(http|https):\/\//,"").split("/")[0]){l.onload=function(){if(!shouldOpen){return}console.log("onload ",l.src,window.location.host);let t=o.pageX;let e=o.pageY;l.style.top=e+30+"px";l.style.left=t+50+"px";l.style.width="500px";l.style.height="600px";l.style.display="block"};l.src=t}}}